mapper.select:
select service.s_no as sNo, service.m_id as mId, s_title as sTitle, s_price as sPrice, s_content as sContent, s_area as sArea, s_img as sImg, s_rate as sRate, main_category as mainCategory, sub_category as subCategory, working_date as workingDate, working_count as workingCount, write_date as writeDate, delete_status as deleteStatus, admin_approval as adminApproval, write_date as writeDate, delete_status as deleteStatus, admin_approval as adminApproval, (select c_name from category where c_no in main_category) as mCatName, (select c_name from category where c_no in sub_category) as sCatName, brand_name as brandName from service,category,member where (service.m_id=member.m_id) and main_category in c_no
order by s_no desc
and s_no=#{_parameter}

mapper.update:
update member set
brand_name = #{brandName},
contact_time = #{contactTime},
introduce =
#{introduce}
where m_id = #{MId}

update service set
admin_approval='y', write_date=to_char(sysdate,'yyyy-mm-dd')
where s_no=#{_parameter}

update service set
delete_status='y',s_title='(삭제)'||(select s_title from service where
s_no=#{_parameter}) where s_no=#{_parameter}

mapper.select:
SELECT brand_name FROM member
JOIN service USING (m_id) where delete_status = 'n'


	and main_category = #{mainCategory}


	and sub_category= #{subCategory}

select s_no as sNo, m_id as mId, s_title as sTitle, s_price as sPrice, s_img as sImg, main_category as mainCategory, sub_category as subCategory, s_rate as sRate from (select rownum as rnum, s. * from (select*from service where delete_status = 'n' and main_category = #{main}
and sub_category= #{sub}
order by 1 desc)s) where rnum between #{start} and #{end}

select
	count(*) cnt
from service where delete_status = 'n'
and main_category = #{main}

	and sub_category= #{sub}
